const Appp=()=>{
    const[name,setName]=useState(0);

    const increm=()=>{
        setName(name+1);
    }

    const decrem=()=>{
        if(name>0 ){
            setName(name-1);
        }else{
            alert('0 limit reached');
            setName(0);
        }

    }
    return(
        <>
        <div ClassName="main_div">
         <div className="center_div">
        <h1>{name}</h1>
        <div className="btn_div">
            <button onClick={increm}>Increm</button>
            <button onClick={decrem}>Decrem</button>
        </div>
        </div>
        </div>
       

        </>
    );
};

export default Appp;
